      <div className="w-32 flex flex-row items-center justify-center text-center text-5xl text-orange-100">
        <img
          className="relative w-px h-[100px] sm:hidden"
          alt=""
          src="/vector-1.svg"
        />
        <b className="flex-1 relative tracking-[0.34px]">{s730}</b>
